import React, { useState, useEffect } from "react";

//to get the data  from Local-Storage
const getLocalItems = () => {
  let list = localStorage.getItem("lists");
  if (list) {
    return JSON.parse(localStorage.getItem("lists"));
  } else {
    return [];
  }
};

function TodoList() {
  const [todo, setTodo] = useState([getLocalItems()]);
  console.log(todo);
  const [input, setInput] = useState("");
  const [toggleSubmit, setToggleSubmit] = useState(true);
  const [isEditItem, setIsEditItem] = useState(null);

  const inputHandler = (e) => {
    setInput(e.target.value);
  };

  const todoHandler = (input) => {
    if (!input) {
      alert("please fill data");
    } else if (input && !toggleSubmit) {
      setTodo(
        todo.map((elem) => {
          if (elem.id === isEditItem) {
            return { ...elem, name: input };
          }
          return elem;
        })
      );
      setToggleSubmit(true);
      setInput("");
      setIsEditItem(null);
    } else {
      const AllInput = { id: new Date().getTime().toString(), name: input };
      console.log(AllInput);
      // const updatedTodo = [AllInput, ...todo];
      setTodo((xyz) => {
        return [...xyz, AllInput];
      });

      setInput("");
    }
  };

  const removeHandler = (index) => {
    console.log(index);
    const updatedItems = todo.filter((elem) => {
      return index !== elem.id;
    });
    setTodo(updatedItems);
  };

  const editItem = (id) => {
    let newEditItem = todo.find((elem) => {
      return elem.id === id;
    });
    console.log(newEditItem);
    setToggleSubmit(false);
    setInput(newEditItem.name);
    setIsEditItem(id);
  };

  //add data to local-storage
  useEffect(() => {
    localStorage.setItem("lists", JSON.stringify(todo));
  }, [todo]);

  return (
    <div className="todo-app">
      <h1>Hello TodoList</h1>
      <input className= "todo-input edit"
        value={input}
        onChange={inputHandler}
        type="text"
        placeholder="what is the todo?"
      />
      {toggleSubmit ? (
        <button className="todo-button" onClick={() => todoHandler(input)}>Add to do</button>
      ) : (
        <button onClick={() => todoHandler(input)}>Update Item</button>
      )}

      {todo.map((elem) => {
        return (
          <div key={elem.id}>
            <h3>{elem.name}</h3>
            <button onClick={() => removeHandler(elem.id)}>Delete</button>
            <button onClick={() => editItem(elem.id)}> Edit</button>
          </div>
        );
      })}
    </div>
  );
}

export default TodoList;
