import React, { useState } from "react";

function TodoList() {
  const [todo, setTodo] = useState([]);
  const [input, setInput] = useState("");

  const inputHandler = (e) => {
    setInput(e.target.value);
  };
  const todoHandler = (input) => {
    const AllInput = { id: new Date().getTime().toString(), name: input };
    console.log(AllInput);
    // const updatedTodo = [AllInput, ...todo];

    setTodo((xyz)=>{
         return [...xyz , AllInput]
    })

    setInput("");
  };

  const removeHandler = (item) => {
    console.log(item);
    const removeArr = [...todo].filter((itemToRemove) => itemToRemove !== item);
    console.log(removeArr);
    setTodo(removeArr);
  };

  return (
    <div>
      <h3>Hello TodoList</h3>
      <input
        onChange={inputHandler}
        type="text"
        placeholder="what is the todo?"
      />
      <button onClick={()=>todoHandler(input)}>Add to do</button>

      {todo.map((elem) => {
        return (
          <div key={elem.id}>
            <h3>{elem}</h3>
            <button onClick={()=>removeHandler(elem.id)}>Delete</button>
          </div>
        );
      })}

      {/* { todo.map((elem)=>{

                <>
                 return

                 (
                     <h3>{elem}</h3>
                     <button>Delete</button>
                     )



                </>

            })} */}
    </div>
  );
}

export default TodoList;
