import React,{useState} from 'react'

function TodoList() {
    const[todo,setTodo] = useState([]);
    const [input,setInput] = useState('');

    const inputHandler = e=>{
        setInput(e.target.value)
    }
    const todoHandler=(item)=>{
        const updatedTodo =[item,...todo]
        setTodo(updatedTodo);
        console.log(updatedTodo)
    }
    return (
        <div>
            <h3>Hello TodoList</h3>
            <input onChange={inputHandler} type="text" placeholder ="what is the todo?" />
            <button onClick={()=>todoHandler(input)}>Add to do</button>
        </div>
    )
}

export default TodoList
