import React,{useState} from 'react'

function TodoList() {
    const[todo,setTodo] = useState([]);
    const [input,setInput] = useState('');

    const inputHandler = e=>{
        setInput(e.target.value)
    }
    const todoHandler=(item)=>{
        const AllInputData = {id: Math.random()*1000 , name:input}
        const updatedTodo =[...todo , AllInputData]
        setTodo(updatedTodo);
        console.log(updatedTodo)
        setInput('')
    }

    const removeHandler = (item)=>{
        console.log(item)
        const removeArr = [...todo].filter((itemToRemove)=>itemToRemove!==item)
        console.log(removeArr)
        setTodo(removeArr)
    }

    return (
        <div>
            <h3>Hello TodoList</h3>
            <input onChange={inputHandler} type="text" placeholder ="what is the todo?" />
            <button onClick={()=>todoHandler(input)}>Add to do</button>
        <div>
            {todo.map((item)=>(
                <h5>{item}
                <button onClick={()=>removeHandler(item)}>Delete</button>
                <button>edit</button>
                </h5>
            ))}
        </div>
        </div>
    )
}

export default TodoList
